<div class="item">	<div class="name">Значение по умолчанию</div>	<table class="radio">		<tr>			<td><label><input type="radio" name="SETTING[DEFAULT]" value="0"<?if(!$_POST['SETTING']['DEFAULT']){?> checked="checked"<?}?>>Нет</label></td><td width="30"></td>			<td><label><input type="radio" name="SETTING[DEFAULT]" value="1"<?if($_POST['SETTING']['DEFAULT']){?> checked="checked"<?}?>>Да</label></td><td></td>		</tr>	</table></div><div class="item">	<div class="name">Показать в виде</div>	<div class="field"><select name="SETTING[VIEW]"><?	$i=0;	$arSettingType = array('Флажков', 'Переключателей');	foreach($arSettingType as $sValue)	{		?><option value="<?=$i?>"<?		if($_POST['SETTING']['VIEW'] == $i){			?> selected<?		}		?>><?=$sValue?></option><?		$i++;	}	?></select></div></div>