<div class="item">	<div class="name">Сущность</div>	<div class="field"><select name="SETTING[TYPE]" id="typeSetting"><?	$i=0;	foreach($arType['INPUT']['SETTING']['TYPE'] as $sKey => $arArray)	{		?><option value="<?=$sKey?>"<?		if($_REQUEST['SETTING']['TYPE'] == $sKey){			?> selected="selected"<?		}		?>><?=$arArray['NAME']?></option><?		$i++;	}	?></select></div></div><div class="item">	<input type="hidden" name="REQUIRED" value="0"><label><input type="checkbox" name="REQUIRED" value="1"<?	if($_POST['REQUIRED']){		?> checked="checked"<?	}	?>>Обязательное для заполнение</label></div><?if(in_array($_REQUEST['SETTING']['TYPE'], array('DATE', 'DATE_TIME'))){	?><div class="item">		<input type="hidden" name="SETTING[DEFAULT_DATE]" value="0">		<label><input type="checkbox" name="SETTING[DEFAULT_DATE]" value="1"<?		if($_POST['SETTING']['DEFAULT_DATE']){			?> checked="checked"<?		}		?>>Установить текущию дату</label>	</div><?}?><script>$(function(){	$('#typeSetting').change(function(){		location.href = '?id=<?=$_ID?>&TYPE=' + $('#type').val() + '&SETTING[TYPE]=' + $('#typeSetting').val() + '&FIELD=' + encodeURIComponent($('input[name=FIELD]').val()) + '&NAME=' + encodeURIComponent($('input[name=NAME]').val());		return false;	});});</script>