<?$arType = $LIB['FIELD']->Type();?><div class="item">	<div class="name">Связь с</div>	<div class="field"><select name="SETTING[TYPE]" id="settingType"><?	$i=0;	$arSettingType = array('SECTION' => 'Разделом', 'USER' => 'Пользователем', 'CATEGORY' => 'Категорией', 'ELEMENT' => 'Элементом', 'YANDEX_MAP' => 'Яндекс картой');	foreach($arSettingType as $sKey => $sName)	{		?><option value="<?=$sKey?>"<?		if($_REQUEST['SETTING']['TYPE'] == $sKey){			?> selected="selected"<?		}		?>><?=$sName?></option><?		$i++;	}	?></select></div></div><?if($_REQUEST['SETTING']['TYPE'] == 'CATEGORY'){	?><div class="item">		<div class="name">Функциональный блок</div>		<div class="field"><select name="SETTING[BLOCK]" id="settingBlock"><?		$arGroup = $LIB['BLOCK_GROUP']->Rows();		for($i=0; $i<count($arGroup); $i++)		{			$arBlock = $LIB['BLOCK']->Rows($arGroup[$i]['ID']);			if(!$i && !$_REQUEST['SETTING']['BLOCK']){				$_REQUEST['SETTING']['BLOCK'] = $arBlock[0]['ID'];			}			?><optgroup label="<?=html($arGroup[$i]['NAME'])?>"><?			for($n=0; $n<count($arBlock); $n++)			{				?><option value="<?=$arBlock[$n]['ID']?>"<?				if($_REQUEST['SETTING']['BLOCK'] == $arBlock[$n]['ID']){					?> selected="selected"<?				}				?>><?=html($arBlock[$n]['NAME'])?></option><?			}			?></optgroup><?		}		?></select></div>	</div><?	$arField = $LIB['FIELD']->Rows('k2_block'.$_REQUEST['SETTING']['BLOCK'].'category');	?>	<div class="item">		<div class="name">Поле выбора</div>		<div class="field"><select name="SETTING[FIELD]" id="settingField"><?		if(!$_POST['SETTING']['FIELD']){			$_POST['SETTING']['FIELD'] = array();		}		$arField = $LIB['FIELD']->Rows('k2_block'.$_REQUEST['SETTING']['BLOCK'].'category');		$arField[-1] = array('FIELD' => 'NAME', 'NAME' => 'Название');       	for($i=-1; $i<count($arField)-1; $i++)		{			?><option value="<?=$arField[$i]['FIELD']?>"<?			if($arField[$i]['FIELD'] == $_POST['SETTING']['FIELD']){				?> selected="selected"<?			}			?>><?=$arField[$i]['NAME']?></option><?		}		?></select></div>	</div><?}if($_REQUEST['SETTING']['TYPE'] == 'ELEMENT'){	?><div class="item">		<div class="name">Функциональный блок</div>		<div class="field"><select name="SETTING[BLOCK]" id="settingBlock"><?		$arGroup = $LIB['BLOCK_GROUP']->Rows();		for($i=0; $i<count($arGroup); $i++)		{			$arBlock = $LIB['BLOCK']->Rows($arGroup[$i]['ID']);			if(!$i && !$_REQUEST['SETTING']['BLOCK']){				$_REQUEST['SETTING']['BLOCK'] = $arBlock[0]['ID'];			}			?><optgroup label="<?=html($arGroup[$i]['NAME'])?>"><?			for($n=0; $n<count($arBlock); $n++)			{				?><option value="<?=$arBlock[$n]['ID']?>"<?				if($_REQUEST['SETTING']['BLOCK'] == $arBlock[$n]['ID']){					?> selected="selected"<?				}				?>><?=html($arBlock[$n]['NAME'])?></option><?			}			?></optgroup><?		}		?></select></div>	</div>	<div class="item">		<div class="name">Поле выбора</div>		<div class="field"><select name="SETTING[FIELD]" id="settingField"><?		if(!$_POST['SETTING']['FIELD']){			$_POST['SETTING']['FIELD'] = array();		}		$arField = $LIB['FIELD']->Rows('k2_block'.$_REQUEST['SETTING']['BLOCK'].'category');		$arField[-1] = array('ID' => -1, 'FIELD' => 'NAME', 'NAME' => 'Название');       	for($i = -1; $i<count($arField) - 1; $i++)		{			?><option value="<?=$arField[$i]['FIELD']?>"<?			if($arField[$i]['FIELD'] == $_POST['SETTING']['FIELD']){				?> selected="selected"<?			}			?>><?=$arField[$i]['NAME']?></option><?		}		?></select></div>	</div><?}?><div class="item">	<input type="hidden" name="REQUIRED" value="0"><label><input type="checkbox" name="REQUIRED" value="1"<?	if($_POST['REQUIRED']){		?> checked="checked"<?	}	?>>Обязательное для заполнение</label></div><?if($_REQUEST['SETTING']['TYPE'] != 'YANDEX_MAP'){	?><div class="item">		<input type="hidden" name="MULTIPLE" value="0"><label><input type="checkbox" name="MULTIPLE" value="1"<?		if($_POST['MULTIPLE']){			?> checked="checked"<?		}		?>>Множественное</label>	</div><?}?><script>$(function(){	$('#settingType, #settingBlock').change(function(){		location.href = '?id=<?=$_ID?>&TYPE=' + $('#type').val() +		'&SETTING[TYPE]=' + $('#settingType').val() +		'&SETTING[BLOCK]=' + $('#settingBlock').val() +		'&FIELD=' + encodeURIComponent($('input[name=FIELD]').val()) +		'&NAME=' + encodeURIComponent($('input[name=NAME]').val());		return false;	});});</script>