tinymce.PluginManager.add('filemanager', function(a, b) {
    a.addButton('filemanager', {
        title:'Загрузить файл',
        icon:'file',
        onclick: function() {
            $.layer({'get':'/k2/admin/system/file-manager/?field='+a.id, 'title':'Файловая библиотека', w:800});
        }
    }), a.addMenuItem('tools111',{
        text:'Загрузить файл',
        context:'insert',
        icon:'file',
        onclick: function() {
            $.layer({'get':'/k2/admin/system/file-manager/?field='+a.id, 'title':'Файловая библиотека', w:800});
        }
    })
});